mapper.select:
SELECT d.smb_id AS id, d.original_id AS originalId, p.p_id AS goodsId, p.name AS goodsName, d.Batchno AS lotNo, p.standard AS specification, p.Factory AS manufacturer, p.unit1Name AS unit, d.quantity AS amount, CONVERT(VARCHAR (100), d.Validdate, 23) AS validateDate, CONVERT(VARCHAR (100), d.MakeDate, 23) AS productDate, p.barcode, b.billnumber AS billNo, d.DealStates AS status, p.serial_number AS number, l1.loc_code AS exportName, l2.loc_code AS importName FROM ${account}.dbo.pda_TranBill_D d LEFT JOIN ${account}.dbo.pda_Products p ON p.p_id = d.p_id LEFT JOIN ${account}.dbo.pda_TranBill b ON b.billid = d.bill_id LEFT JOIN ${account}.dbo.pda_location l1 ON l1.l_id = d.Location_id LEFT JOIN ${account}.dbo.pda_location l2 ON l2.l_id = d.location_id2 WHERE d.DealStates = 0
AND b.billnumber = #{billNo}
AND p.barcode = #{goodsCode}
AND b.sout_id = #{sId}
AND b.sa_outid = #{saId}

mapper.update:
UPDATE ${account}.dbo.pda_TranBill_D SET DealStates = 1, wctime = getdate(), loginid = #{userId} WHERE original_id = #{id}
SELECT count(*) AS total FROM ${account}.dbo.pda_TranBill_D d WHERE d.DealStates = 0 AND d.bill_id = (SELECT DISTINCT d1.bill_id FROM ${account}.dbo.pda_TranBill_D d1 WHERE d1.original_id = #{id})
UPDATE ${account}.dbo.pda_TranBill SET billstates = 3 WHERE billid = (SELECT DISTINCT d.bill_id FROM ${account}.dbo.pda_TranBill_D d WHERE d.original_id = #{id})

mapper.select:
SELECT
b.billid AS id,
b.billnumber AS name
FROM ${account}.dbo.pda_TranBill b


        b.sa_outid = #{saId}


        AND b.sout_id = #{sId}

SELECT DISTINCT d.original_id AS id, p.barcode AS name FROM ${account}.dbo.pda_TranBill_D d INNER JOIN ${account}.dbo.pda_Products p ON d.p_id = p.p_id LEFT JOIN ${account}.dbo.pda_TranBill b ON d.bill_id = b.billid
b.sa_outid = #{saId}
AND b.sout_id = #{sId}

